import React, { useState, useEffect } from 'react';
import { Modal, Button } from 'react-bootstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEdit, faTrash, faPlus } from '@fortawesome/free-solid-svg-icons';
import axios from 'axios';

const Category = () => {
  const [categories, setCategories] = useState([]);
  const [name, setName] = useState('');
  const [isEditing, setIsEditing] = useState(false);
  const [editId, setEditId] = useState(null);
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    fetchCategories();
  }, []);

  const fetchCategories = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/categories');
      setCategories(response.data);
    } catch (error) {
      console.error('Error fetching categories:', error);
    }
  };

  const addCategory = async (e) => {
    e.preventDefault();
    if (!name) return;

    try {
      const newCategory = { name };
      const response = await axios.post('http://localhost:5000/api/categories', newCategory);
      setCategories([...categories, response.data]);
      resetForm();
    } catch (error) {
      console.error('Error adding category:', error);
    }
  };

  const updateCategory = async (e) => {
    e.preventDefault();
    if (!name) return;

    try {
      const updatedCategory = { name };
      const response = await axios.put(`http://localhost:5000/api/categories/${editId}`, updatedCategory);
      setCategories(categories.map(category =>
        category._id === editId ? response.data : category
      ));
      resetForm();
    } catch (error) {
      console.error('Error updating category:', error);
    }
  };

  const deleteCategory = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/categories/${id}`);
      setCategories(categories.filter(category => category._id !== id));
    } catch (error) {
      console.error('Error deleting category:', error);
    }
  };

  const editCategory = (id) => {
    const category = categories.find(cat => cat._id === id);
    setName(category.name);
    setIsEditing(true);
    setEditId(id);
    setShowModal(true);
  };

  const resetForm = () => {
    setName('');
    setIsEditing(false);
    setEditId(null);
    setShowModal(false);
  };

  return (
    <div className="content">
      <h2>Categories</h2>
      <Button onClick={() => setShowModal(true)} className="btn btn-primary mb-3">
        <FontAwesomeIcon icon={faPlus} /> Add
      </Button>
      <table className="table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {categories.map((category) => (
            <tr key={category._id}>
              <td>{category._id}</td>
              <td>{category.name}</td>
              <td>
                <Button onClick={() => editCategory(category._id)} className="btn btn-secondary btn-sm">
                  <FontAwesomeIcon icon={faEdit} /> 
                </Button>
                <Button onClick={() => deleteCategory(category._id)} className="btn btn-danger btn-sm" style={{ marginLeft: '10px' }}>
                  <FontAwesomeIcon icon={faTrash} /> 
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <Modal show={showModal} onHide={resetForm}>
        <Modal.Header closeButton>
          <Modal.Title>{isEditing ? 'Edit Category' : 'Add Category'}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <form onSubmit={isEditing ? updateCategory : addCategory}>
            <div className="mb-3">
              <label htmlFor="name" className="form-label">Name</label>
              <input
                type="text"
                className="form-control"
                id="name"
                value={name}
                onChange={(e) => setName(e.target.value)}
              />
            </div>
            <Button type="submit" className="btn btn-primary">
              {isEditing ? 'Update' : 'Add'} Category
            </Button>
            <Button onClick={resetForm} className="btn btn-secondary ml-2">
              Cancel
            </Button>
          </form>
        </Modal.Body>
      </Modal>
    </div>
  );
};

export default Category;
