import React, { useState, useEffect } from 'react';

const Expenses = () => {
  // State for expenses and form inputs
  const [expenses, setExpenses] = useState([]);
  const [description, setDescription] = useState('');
  const [amount, setAmount] = useState('');
  const [isEditing, setIsEditing] = useState(false);
  const [editId, setEditId] = useState(null);

  // Dummy initial data
  useEffect(() => {
    setExpenses([
      { id: 1, description: 'Groceries', amount: 50 },
      { id: 2, description: 'Utilities', amount: 100 },
      { id: 3, description: 'Rent', amount: 800 },
      { id: 4, description: 'Entertainment', amount: 50 },
      { id: 5, description: 'Transportation', amount: 70 },
    ]);
  }, []);

  // Add a new expense
  const addExpense = (e) => {
    e.preventDefault();
    if (!description || !amount) return;

    const newExpense = {
      id: expenses.length + 1,
      description,
      amount: parseFloat(amount),
    };

    setExpenses([...expenses, newExpense]);
    setDescription('');
    setAmount('');
  };

  // Update an existing expense
  const updateExpense = (e) => {
    e.preventDefault();
    if (!description || !amount) return;

    setExpenses(expenses.map(expense =>
      expense.id === editId ? { ...expense, description, amount: parseFloat(amount) } : expense
    ));
    setDescription('');
    setAmount('');
    setIsEditing(false);
    setEditId(null);
  };

  // Delete an expense
  const deleteExpense = (id) => {
    setExpenses(expenses.filter(expense => expense.id !== id));
  };

  // Set the expense to be edited
  const editExpense = (id) => {
    const expense = expenses.find(exp => exp.id === id);
    setDescription(expense.description);
    setAmount(expense.amount);
    setIsEditing(true);
    setEditId(id);
  };

  return (
    <div className="content">
      <h2>Expenses</h2>
      <form onSubmit={isEditing ? updateExpense : addExpense} style={{ marginBottom: '20px' }}>
        <div className="mb-3">
          <label htmlFor="description" className="form-label">Description</label>
          <input
            type="text"
            className="form-control"
            id="description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />
        </div>
        <div className="mb-3">
          <label htmlFor="amount" className="form-label">Amount</label>
          <input
            type="number"
            className="form-control"
            id="amount"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
          />
        </div>
        <button type="submit" className="btn btn-primary">{isEditing ? 'Update' : 'Add'} Expense</button>
      </form>
      <table className="table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Description</th>
            <th>Amount</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {expenses.map((expense) => (
            <tr key={expense.id}>
              <td>{expense.id}</td>
              <td>{expense.description}</td>
              <td>${expense.amount}</td>
              <td>
                <button onClick={() => editExpense(expense.id)} className="btn btn-secondary btn-sm">Edit</button>
                <button onClick={() => deleteExpense(expense.id)} className="btn btn-danger btn-sm" style={{ marginLeft: '10px' }}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Expenses;
