import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Card, Button, Form, Spinner } from 'react-bootstrap';

const Dashboard = () => {
  const [categorySummary, setCategorySummary] = useState([]);
  const [filteredExpenses, setFilteredExpenses] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchCategorySummary();
    fetchCategories();
  }, []);

  const fetchCategorySummary = async () => {
    try {
      const response = await axios.get('http://localhost:5000/category-summary');
      setCategorySummary(response.data);
    } catch (error) {
      console.error('Error fetching category summary:', error);
    }
  };

  const fetchCategories = async () => {
    try {
      // Fetch categories from your endpoint
      const response = await axios.get('http://localhost:5000/categories');
      setCategories(response.data);
    } catch (error) {
      console.error('Error fetching categories:', error);
    }
  };

  const handleFilter = async () => {
    setLoading(true);
    try {
      const response = await axios.get('http://localhost:5000/date-range', {
        params: {
          startDate,
          endDate,
        },
      });
      setFilteredExpenses(response.data);
    } catch (error) {
      console.error('Error fetching filtered expenses:', error);
    }
    setLoading(false);
  };

  const downloadReport = (data, filename) => {
    const blob = new Blob([JSON.stringify(data, null, 2)], { type: 'application/json' });
    const url = URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.href = url;
    link.download = filename;
    link.click();
    URL.revokeObjectURL(url);
  };

  return (
    <div className="container mt-4">
      <div className="row">
        <div className="col-md-6">
          <Card>
            <Card.Body>
              <Card.Title>Category-wise Amount</Card.Title>
              <ul>
                {categorySummary.map((item) => (
                  <li key={item.category}>
                    <strong>{item.category}:</strong> ${item.totalAmount}
                  </li>
                ))}
              </ul>
              <Button 
                variant="primary" 
                onClick={() => downloadReport(categorySummary, 'category-summary.json')}
              >
                Download Report
              </Button>
            </Card.Body>
          </Card>
        </div>
        <div className="col-md-6">
          <Card>
            <Card.Body>
              <Card.Title>Filter Expenses</Card.Title>
              <Form>
                <Form.Group>
                  <Form.Label>Category</Form.Label>
                  <Form.Control 
                    as="select" 
                    value={selectedCategory} 
                    onChange={(e) => setSelectedCategory(e.target.value)}
                  >
                    <option value="">Select Category</option>
                    {categories.map((cat) => (
                      <option key={cat._id} value={cat.category}>
                        {cat.category}
                      </option>
                    ))}
                  </Form.Control>
                </Form.Group>
                <Form.Group>
                  <Form.Label>Start Date</Form.Label>
                  <Form.Control 
                    type="date" 
                    value={startDate} 
                    onChange={(e) => setStartDate(e.target.value)}
                  />
                </Form.Group>
                <Form.Group>
                  <Form.Label>End Date</Form.Label>
                  <Form.Control 
                    type="date" 
                    value={endDate} 
                    onChange={(e) => setEndDate(e.target.value)}
                  />
                </Form.Group>
                <Button 
                  variant="primary" 
                  onClick={handleFilter}
                  className="mt-2"
                >
                  Filter
                </Button>
              </Form>
              {loading && <Spinner animation="border" />}
              {filteredExpenses.length > 0 && (
                <div className="mt-3">
                  <Button 
                    variant="primary" 
                    onClick={() => downloadReport(filteredExpenses, 'filtered-expenses.json')}
                  >
                    Download Report
                  </Button>
                </div>
              )}
            </Card.Body>
          </Card>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
