import React, { useState, useEffect } from 'react';
import { Modal, Button } from 'react-bootstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEdit, faTrash, faPlus } from '@fortawesome/free-solid-svg-icons';
import axios from 'axios';

const Category = () => {
  const [categories, setCategories] = useState([]);
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [isEditing, setIsEditing] = useState(false);
  const [editId, setEditId] = useState(null);
  const [showModal, setShowModal] = useState(false);

  // Fetch categories from the API on component mount
  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/categories');
        setCategories(response.data);
      } catch (error) {
        console.error('Error fetching categories:', error);
      }
    };

    fetchCategories();
  }, []);

  // Add a new category
  const addCategory = async (e) => {
    e.preventDefault();
    if (!name || !description) return;

    try {
      const response = await axios.post('/api/categories', { name, description });
      setCategories([...categories, response.data]);
      resetForm();
    } catch (error) {
      console.error('Error adding category:', error);
    }
  };

  // Update an existing category
  const updateCategory = async (e) => {
    e.preventDefault();
    if (!name || !description) return;

    try {
      const response = await axios.put(`/api/categories/${editId}`, { name, description });
      setCategories(categories.map(category =>
        category.id === editId ? response.data : category
      ));
      resetForm();
    } catch (error) {
      console.error('Error updating category:', error);
    }
  };

  // Delete a category
  const deleteCategory = async (id) => {
    try {
      await axios.delete(`/api/categories/${id}`);
      setCategories(categories.filter(category => category.id !== id));
    } catch (error) {
      console.error('Error deleting category:', error);
    }
  };

  // Set up category for editing
  const editCategory = (id) => {
    const category = categories.find(cat => cat.id === id);
    setName(category.name);
    setDescription(category.description);
    setIsEditing(true);
    setEditId(id);
    setShowModal(true);
  };

  // Reset form and close modal
  const resetForm = () => {
    setName('');
    setDescription('');
    setIsEditing(false);
    setEditId(null);
    setShowModal(false);
  };

  return (
    <div className="content">
      <h2 style={{ position: 'fixed', top: '10px', left: '220px', zIndex: '1000' }}>Categories</h2>
      <Button
        style={{ left: '220px', zIndex: '1000' }}
        onClick={() => setShowModal(true)}
        className="btn btn-primary mb-3"
      >
        <FontAwesomeIcon icon={faPlus} /> Add
      </Button>
      <div style={{ position: 'relative' }}>
        <table className="table" style={{ padding: '10px' }}>
          <thead style={{ position: 'sticky', top: '0', backgroundColor: 'white', zIndex: '1000' }}>
            <tr>
              <th>Name</th>
              <th>Description</th>
              <th>Actions</th>
            </tr>
          </thead>
        </table>
        <div style={{ maxHeight: '400px', overflowY: 'scroll' }}>
          <table className="table">
            <tbody>
              {categories.map((category) => (
                <tr key={category.id}>
                  <td>{category.name}</td>
                  <td>{category.description}</td>
                  <td>
                    <Button onClick={() => editCategory(category.id)} className="btn btn-secondary btn-sm">
                      <FontAwesomeIcon icon={faEdit} />
                    </Button>
                    <Button onClick={() => deleteCategory(category.id)} className="btn btn-danger btn-sm" style={{ marginLeft: '10px' }}>
                      <FontAwesomeIcon icon={faTrash} />
                    </Button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      <Modal show={showModal} onHide={resetForm}>
        <Modal.Header closeButton>
          <Modal.Title>{isEditing ? 'Edit Category' : 'Add Category'}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <form onSubmit={isEditing ? updateCategory : addCategory}>
            <div className="mb-3">
              <label htmlFor="name" className="form-label">Name</label>
              <input
                type="text"
                className="form-control"
                id="name"
                value={name}
                onChange={(e) => setName(e.target.value)}
              />
            </div>
            <div className="mb-3">
              <label htmlFor="description" className="form-label">Description</label>
              <input
                type="text"
                className="form-control"
                id="description"
                value={description}
                onChange={(e) => setDescription(e.target.value)}
              />
            </div>
            <Button type="submit" className="btn btn-primary">
              {isEditing ? 'Update' : 'Add'} Category
            </Button>
            <Button onClick={resetForm} className="btn btn-secondary ml-2">
              Cancel
            </Button>
          </form>
        </Modal.Body>
      </Modal>
    </div>
  );
};

export default Category;
