import React from 'react';
import { Link } from 'react-router-dom';

const Sidebar = () => {
  // Dummy expenses data (replace with actual data or fetch from API)
  const expenses = [
    { id: 1, description: 'Groceries', amount: 50 },
    { id: 2, description: 'Utilities', amount: 100 },
    { id: 3, description: 'Rent', amount: 800 },
    { id: 4, description: 'Entertainment', amount: 50 },
    { id: 5, description: 'Transportation', amount: 70 },
  ];

  return (
    <nav id="sidebar" className="bg-light sidebar">
      <div className="position-sticky">
        <div className="d-flex justify-content-between align-items-center py-3 px-4">
          <h4 className="m-0">Expenses</h4>
          {/* Add a link to add new expense or other actions */}
        </div>
        <ul className="nav flex-column mb-auto">
          {expenses.map((expense) => (
            <li key={expense.id} className="nav-item">
              <Link className="nav-link" to={`/expenses/${expense.id}`}>
                {expense.description} - ${expense.amount}
              </Link>
            </li>
          ))}
        </ul>
      </div>
    </nav>
  );
}

export default Sidebar;
