import React, { useState, useEffect } from 'react';
import { Modal, Button } from 'react-bootstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEdit, faTrash, faPlus } from '@fortawesome/free-solid-svg-icons';
import axios from 'axios';

const Category = () => {
  const [categories, setCategories] = useState([]);
  const [selectedCategoryId, setSelectedCategoryId] = useState('');
  const [categoryDescription, setCategoryDescription] = useState('');
  const [description, setDescription] = useState('');
  const [isEditing, setIsEditing] = useState(false);
  const [editId, setEditId] = useState(null);
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    fetchCategories();
  }, []);

  const fetchCategories = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/categories');
      setCategories(response.data);
    } catch (error) {
      console.error('Error fetching categories:', error);
    }
  };

  const addCategory = async (e) => {
    e.preventDefault();
    if (!selectedCategoryId || !description) return;

    try {
      const selectedCategory = categories.find(cat => cat._id === selectedCategoryId);
      const response = await axios.post('http://localhost:5000/api/categories', {
        category: selectedCategory.category,
        description
      });
      setCategories([...categories, response.data]);
      resetForm();
    } catch (error) {
      console.error('Error adding category:', error);
    }
  };

  const updateCategory = async (e) => {
    e.preventDefault();
    if (!selectedCategoryId || !description) return;

    try {
      const response = await axios.put(`http://localhost:5000/api/categories/${editId}`, { 
        category: selectedCategoryId,
        description 
      });
      setCategories(categories.map(cat =>
        cat._id === editId ? response.data : cat
      ));
      resetForm();
    } catch (error) {
      console.error('Error updating category:', error);
    }
  };

  const deleteCategory = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/categories/${id}`);
      setCategories(categories.filter(cat => cat._id !== id));
    } catch (error) {
      console.error('Error deleting category:', error);
    }
  };

  const editCategory = (id) => {
    const cat = categories.find(c => c._id === id);
    setSelectedCategoryId(cat._id);
    setCategoryDescription(cat.category);
    setDescription(cat.description);
    setIsEditing(true);
    setEditId(id);
    setShowModal(true);
  };

  const handleCategoryChange = (e) => {
    const selectedCategory = categories.find(cat => cat._id === e.target.value);
    setSelectedCategoryId(e.target.value);
    setCategoryDescription(selectedCategory.category);
    setDescription(selectedCategory.description);
  };

  const resetForm = () => {
    setSelectedCategoryId('');
    setCategoryDescription('');
    setDescription('');
    setIsEditing(false);
    setEditId(null);
    setShowModal(false);
  };

  return (
    <div className="content">
      <h2>Categories</h2>
      <Button onClick={() => setShowModal(true)} className="btn btn-primary mb-3">
        <FontAwesomeIcon icon={faPlus} /> Add
      </Button>
      <table className="table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Category</th>
            <th>Description</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {categories.map((cat) => (
            <tr key={cat._id}>
              <td>{cat._id}</td>
              <td>{cat.category}</td>
              <td>{cat.description}</td>
              <td>
                <Button onClick={() => editCategory(cat._id)} className="btn btn-secondary btn-sm">
                  <FontAwesomeIcon icon={faEdit} />
                </Button>
                <Button onClick={() => deleteCategory(cat._id)} className="btn btn-danger btn-sm" style={{ marginLeft: '10px' }}>
                  <FontAwesomeIcon icon={faTrash} />
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <Modal show={showModal} onHide={resetForm}>
        <Modal.Header closeButton>
          <Modal.Title>{isEditing ? 'Edit Category' : 'Add Category'}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <form onSubmit={isEditing ? updateCategory : addCategory}>
            <div className="mb-3">
              <label htmlFor="category" className="form-label">Category</label>
              <select
                className="form-control"
                id="category"
                value={selectedCategoryId}
                onChange={handleCategoryChange}
              >
                <option value="">Select a category</option>
                {categories.map(cat => (
                  <option key={cat._id} value={cat._id}>
                    {cat.category}
                  </option>
                ))}
              </select>
            </div>
            <div className="mb-3">
              <label htmlFor="description" className="form-label">Description</label>
              <input
                type="text"
                className="form-control"
                id="description"
                value={description}
                onChange={(e) => setDescription(e.target.value)}
              />
            </div>
            <Button type="submit" className="btn btn-primary">
              {isEditing ? 'Update' : 'Add'} Category
            </Button>
            <Button onClick={resetForm} className="btn btn-secondary ml-2">
              Cancel
            </Button>
          </form>
        </Modal.Body>
      </Modal>
    </div>
  );
};

export default Category;
